def getExtensionFromContentType(contentType=None):
    match contentType:
        case "audio/mpeg":
            return "mp3"
        case "audio/aac":
            return "aac"
        case "audio/wav":
            return "wav"
        case "audio/ogg":
            return "ogg"
        case "audio/mp4":
            return "m4a"
        case "audio/flac":
            return "flac"
        case "audio/x-m4a":
            return "m4a"
    
    # if unmatched or not provided, send back a exception
    raise ValueError("Error: contentType provided is unknown to the codebase.")    
    return None # this line is unreachable

def isUnique(list, query):
    # test the list to determine if the 'query' item already exists in the list
    # Return False if the query is in the list
    # Return True if the element is not in the list (it 'isUnqiue')
    for element in list:
        if query == element:
            return False
    
    return True

def dump_text_to_file(textbox, logtype, folder):
 # this will always write a new file but should never have a filename collision because of the timestamp
    log_file_path = os.path.join(folder, "logs")
    os.makedirs(log_file_path, exist_ok=True)
    try:
        with open(os.path.join(log_file_path, f"YJE-{datetime.today().strftime('%Y-%m-%d_%H-%M-%S')}-{logtype}.log"), 'w', encoding="utf-8") as log_file:
            log_file.write(textbox.get("0.0", "end"))
            log_file.close()
            announce_message(f"{logtype} written to file: {log_file_path}", MESSAGE_TYPES['info'])
    except IOError as ex:
            announce_message(f"Error writing {logtype} to file: {log_file_path}", MESSAGE_TYPES['error'], ex)

